import React, { useState, useEffect } from 'react';
import { BrowserRouter, Routes, Route, useNavigate } from 'react-router-dom';
import Navbar from './Navbar';
import Home from './Home';
import About from './About';
import Contact from './Contact';
import Team from './Team';
import Error from './Error';
import Signup from './Signup';
import Login from './Login';
import SimpleSlider from './SimpleSlider';
import ScrollButton from './ScrollButton';
import Loading from './Loading';

const App = () => {
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    // Define the handleRouteChange function inside useEffect
    const handleRouteChange = () => {
      // Reload the page when a new route is navigated
      window.location.reload();
    };

    // Attach the handleRouteChange function to the navigate event
    navigate(handleRouteChange);

    // Clear the navigate event when the component unmounts
    return () => {
      navigate(handleRouteChange);
    };

    // Set loading to false after a delay
    setTimeout(() => setLoading(false), 1000);
  }, [navigate]);

  return (
    <>
      {loading ? (
        <Loading />
      ) : (
        <BrowserRouter>
          <Navbar />
          <ScrollButton />
          <Routes>
            <Route path="/" element={<Home />} />
            <Route path="/about" element={<About />} />
            <Route path="/contact" element={<Contact />} />
            <Route path="/about/team" element={<Team />} />
            <Route path="/login" element={<Login />} />
            <Route path="/signup" element={<Signup />} />
            <Route path="/error" element={<Error />} />
            <Route path="/slider" element={<SimpleSlider />} />
          </Routes>
        </BrowserRouter>
      )}
    </>
  );
};

export default App;
