import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import ServiceData from "./ServicesData";
import CustomCard from "./CustomCard";
import Grid from "@mui/material/Grid";
import { useState } from "react";

const SimpleSlider = ({ itemsWithHoverEffect }) => {
  const settings = {
    dots: true,
    infinite: true,
    slidesToShow: 3,
    slidesToScroll: 1,
    autoplay: true,
    autoplaySpeed: 2000,
    pauseOnHover: true,
    initialSlide: 0,
    responsive: [
      {
        breakpoint: 1024,
        settings: {
          slidesToScroll: 1,
          infinite: true,
          dots: true,
        },
      },

      {
        breakpoint: 900,
        settings: {
          slidesToShow: 3,
          slidesToScroll: 1,
          infinite: true,
          dots: true,
        },
      },
      {
        breakpoint: 600,
        settings: {
          slidesToShow: 2,
          slidesToScroll: 1,
          dots: true,
        },
      },
      {
        breakpoint: 480,
        settings: {
          slidesToShow: 1,
          slidesToScroll: 1,
          dots: true,
        },
      },
    ]
    };

  const [hoveredIndex, setHoveredIndex] = useState(null);

  return (
    <>
      <Slider {...settings}>
        {ServiceData.map((val, id) => (
          <Grid item xs={11} sm={11} md={11} key={id}>
            <CustomCard
              image={val.image}
              title={val.title}
              rating={val.rating}
              price={val.price}
              isHovered={hoveredIndex === id && itemsWithHoverEffect.includes(id)}
              onMouseEnter={() => setHoveredIndex(id)}
              onMouseLeave={() => setHoveredIndex(null)}
            />
          </Grid>
        ))}
      </Slider>
    </>
  );
};

export default SimpleSlider;
