import React from "react";
import TextField from "@mui/material/TextField";
import Button from "@mui/material/Button";
import Box from "@mui/material/Box";
import Grid from "@mui/material/Grid";
import EmailIcon from "@mui/icons-material/Email";
import Typography from "@mui/material/Typography";

const Newsletter = () => {
  return (
    <>
      <Box
        sx={{
          paddingTop: "80px",
          flexGrow: 1,
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
        }}
      >
        <Grid container spacing={2} style={{ backgroundColor: "#f3f6fd" }}>
          <Grid item xs={12} md={7}>
            <Box
              sx={{
                p: { xs: 1, sm: 2, md: 3 },
                display: "flex",
                justifyContent: {
                  xs: "center",
                  md: "center",
                  lg: "flex-start",
                },
                flexDirection: {
                  xs: "column",
                  sm: "column",
                  md: "row",
                  lg: "row",
                },
                alignItems: "center",
              }}
            >
              <Typography
                variant="h5"
                component="h5"
                sx={{
                  display: "flex",
                  justifyContent: {xs:"center", sm:'center', md:'center', lg:'flex-start'},
                  flexDirection: {
                    xs: "column",
                    sm: "column",
                    md: "column",
                    lg: "row",
                  },
                  textAlign: "center",
                }}
              >
                <EmailIcon
                  color="secondary"
                  sx={{
                    fontSize: { xs: "60px" ,sm:"60px" ,md: "80px", flexDirection: {
                      xs: "column",
                      sm: "column",
                      md: "column",
                      lg: "row",
                    }, },
                  }}
                />
                <div style={{}}>
                  <Typography variant="h5">
                    Get the latest news and offers
                  </Typography>
                  <Typography variant="" sx={{ fontWeight: 700 }}>
                    Subscribe to our newsletter
                  </Typography>
                </div>
              </Typography>
            </Box>
          </Grid>

          <Grid item xs={12} md={5}>
            <Box
              sx={{
                marginTop: { xs: "20px", md: "50px" },
                padding: "10px",
                display: "flex",
                justifyContent: { xs: "center", md: "center", lg: "end" },
                alignItems: "flex-start",
              }}
            >
              <TextField
                label="Enter your mail address"
                variant="outlined"
                color="secondary"
                fullWidth
              />
              <Button
                variant="contained"
                size="large"
                color="secondary"
                sx={{
                  padding: { xs: "15px 30px", sm: "15px 30px" },
                  ":hover": {
                    backgroundColor: "#212529",
                  },
                }}
              >
                Subscribe
              </Button>
            </Box>
          </Grid>
        </Grid>
      </Box>
    </>
  );
};

export default Newsletter;
