import React, { useState, useEffect } from 'react';
import { BrowserRouter, Routes, Route, Link, Outlet } from 'react-router-dom';
import Navbar from './Navbar';
import Home from './Home';
import About from './About';
import Contact from './Contact';
import Team from './Team';
import Error from './Error';
import Signup from './Signup';
import Login from './Login';
import SimpleSlider from './SimpleSlider';
import ScrollButton from './ScrollButton';
import Loading from './Loading';

const LoadingRoute = ({ element }) => {
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    setTimeout(() => setLoading(false), 3000); // Adjust the loading time as needed
  }, []);

  return (
    <div>
      {loading ? <Loading /> : element}
    </div>
  );
};

const App = () => {
  return (
    <BrowserRouter>
      <Navbar />
      <ScrollButton />
      <Routes>
        <Route
          path="/"
          element={<LoadingRoute element={<Home />} />}
        />
        <Route
          path="/about"
          element={<LoadingRoute element={<About />} />}
        />
        <Route
          path="/contact"
          element={<LoadingRoute element={<Contact />} />}
        />
        <Route path="/about/team" element={<Team />} />
        <Route path="/login" element={<Login />} />
        <Route path="/signup" element={<Signup />} />
        <Route path="/error" element={<Error />} />
        <Route path="/slider" element={<SimpleSlider />} />
      </Routes>
    </BrowserRouter>
  );
};

export default App;
