import React, { useState, useEffect } from 'react';
import { BrowserRouter, Routes, Route, Link } from 'react-router-dom';
import Navbar from './Navbar';
import Home from './Home';
import About from './About';
import Contact from './Contact';
import Team from './Team';
import Error from './Error';
import Signup from './Signup';
import Login from './Login';
import SimpleSlider from './SimpleSlider';
import ScrollButton from './ScrollButton';
import Loading from './Loading';

const App = () => {
  const [loading, setLoading] = useState(false);

  // Function to trigger the loading screen
  const triggerLoadingScreen = () => {
    setLoading(true);

    // Simulate a delay before the page loads
    setTimeout(() => {
      setLoading(false);
    }, 3000); // Adjust the delay as needed
  };

  return (
    <div>
      {loading ? (
        <Loading />
      ) : (
        <BrowserRouter>
          <Navbar />
          <ScrollButton />
          <Routes>
            <Route path="/" element={<Home />} />
            <Route path="/about" element={<About />} />
            <Route path="/contact" element={<Contact />} />
            <Route path="/about/team" element={<Team />} />
            <Route path="/login" element={<Login />} />
            <Route path="/signup" element={<Signup />} />
            <Route path="/error" element={<Error />} />
            <Route path="/slider" element={<SimpleSlider />} />
            <Route path="*" element={<Error />} />
          </Routes>
        </BrowserRouter>
      )}

      {/* Add navigation links that trigger the loading screen */}
      <nav>
        <ul>
          <li>
            <Link to="/" onClick={triggerLoadingScreen}>
              Home
            </Link>
          </li>
          <li>
            <Link to="/about" onClick={triggerLoadingScreen}>
              About
            </Link>
          </li>
          <li>
            <Link to="/contact" onClick={triggerLoadingScreen}>
              Contact
            </Link>
          </li>
        </ul>
      </nav>
    </div>
  );
};

export default App;
